var prevScrollpos = window.pageYOffset;
window.onscroll = function() {
  var width = document.body.clientWidth;
  var currentScrollPos = window.pageYOffset;
  const element = document.getElementById("header-navbar");
  const elementInfo = document.getElementById("header-info");
  if (prevScrollpos > currentScrollPos) {
    document.getElementById("header-info").style.top = "0";
	elementInfo.getElementsByClassName("elementor-background-overlay")[0].style.backgroundColor = "#000000c9"
// 	document.getElementById("header-navbar-inner").style.padding = "0 0 10px 0";
	  if(width > 768) {
		  element.style.marginTop = "49.9px";
	  } else if (width > 480) {
		  element.style.marginTop = "0";
	  }else {
		  element.style.marginTop = "0";
	  }
	
  } else {
    document.getElementById("header-info").style.top = "-50px";
	 element.getElementsByClassName("elementor-background-overlay")[0].style.backgroundColor = "#000000c9"
	 element.style.marginTop = "0";
// 	 document.getElementById("header-navbar-inner").style.padding = "10px";
  }
  prevScrollpos = currentScrollPos;
}
